import React from "react";
import {useState,useEffect} from "react";
import {useDispatch,useSelector} from 'react-redux'
import { getPokemons,getTypes,filterPokeByType,filterPokeByCreated, sortByName } from "../actions";
import { Link } from "react-router-dom";
import Card from "./Card";
import Paginado from "./Paginado";




export default function Home(){
    const dispatch= useDispatch()
    

    const allPokemons=useSelector((state)=>state.pokemons)
    useEffect(()=>{
        dispatch(getPokemons());
    },[dispatch])
    
    const allTypes=useSelector((state)=>state.types)
    useEffect(()=>{
        dispatch(getTypes());
    },[dispatch])

    const[currentPage,setCurrentPage]=useState(1)
    const [pokePerPage, setPokePerPage]=useState(12)
    const indexOfLastPoke=currentPage * pokePerPage
    const indexOfFirstPoke=indexOfLastPoke - pokePerPage
    const currentPoke= allPokemons.slice(indexOfFirstPoke,indexOfLastPoke)

    const paginado=(pageNumber)=>{
        setCurrentPage(pageNumber)
    }


    function handleClick(e){
        e.preventDefault();
        dispatch(getPokemons());

    }

    function handleFilterType(e){
        dispatch(filterPokeByType(e.target.value));

    }

    function handlefilterPokeByCreated(e){
        dispatch(filterPokeByCreated(e.target.value))
    }

    function handleSortByName (e){
        e.preventDefault();
        dispatch(sortByName(e.target.value))
        setCurrentPage(1);
        setOrden(`Ordenado ${e.target.value}`)
    }

    return(
<div>
    <Link to='/character'>Create Pokémon</Link>
    <h1>PI Pokémon</h1>
    <button onClick={e=>{handleClick(e)}}>
        Volver a cargar todos los pokémons
    </button>
    <div>
           <select >
           <option value="" hidden>Alfabético</option>
            <option value='asc'>Ascendente</option>
            <option value='desc'>Descendente</option>

           </select>

           <select>
           <option value="" hidden>Ataque</option>
           <option value='asc'>Ascendente</option>
            <option value='desc'>Descendente</option>
           </select>

           <select onChange={e=> handlefilterPokeByCreated(e)}>
           <option value="" hidden>Creados</option>
            <option value='all'>TODOS</option>
            <option  value='created'>Creados</option>
            <option value='api'>Api</option>
           </select>

           <select onChange={e=> handleFilterType(e)}>
           <option value="" hidden>Tipo</option>
            <option value='all'>TODOS</option>
            {
                allTypes?.map(el=>(
                    <option key={el.id} value={el.name}>{el.name}</option>
                ))

            }
           </select>
           <Paginado
           pokePerPage={pokePerPage}
            allPokemons={allPokemons.length}
            paginado={paginado}/>
           <div>
         
           {
    currentPoke?.map(el=>{
        return(
            <div className="Cards" key={el.id}>
            <Link to={"/home"+ el.id} key={el.id}>
            <Card name={el.name} type={el.type} img={el.img} key={el.id}/>
            </Link>
            </div>
        );
       
    })}
    
           </div>

           

        


    </div>
</div>
    )
}


// Botones/Opciones para filtrar por tipo de pokemon y por pokemon existente o creado por nosotros
// [ ] Botones/Opciones para ordenar tanto ascendentemente como descendentemente los pokemons por orden alfabético y por ataque
// [ ] Paginado para ir buscando y mostrando los siguientes pokemons, 12 pokemons por pagina.
